mapper.update:
update poor_stu
set s_name=#{s_name}, s_sex=#{s_sex},
	s_major=#{s_major}, s_email = #{s_email},s_phone=#{s_phone}
where stu_id=#{stu_id}

update poor_stu
set s_income = #{s_income}, s_outcome = #{s_outcome},
	s_info = #{s_info}, s_other = #{s_other},poor_level=#{poor_level}
where stu_id=#{stu_id}
